import React, { useState } from 'react';
import ReactDOM from 'react-dom';
//import { useRouter } from 'next/router';
import {API_SERVER as API_SERVER} from '../src/constants';
import Cookies from 'universal-cookie';
//import UserMenu from '../components/UserMenu';
import Header from '../components/Header';
import PayPal from '../components/Paypal';
import Nav from '../components/Nav';
import Footer from '../components/Footer';
import Ping from '../components/Ping';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
//import 'bootstrap/dist/css/bootstrap.min.css';
import "../src/styles.css";
const cookies = new Cookies();
const session = cookies.get('session');

export default class Index extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      myEmail: '',
      loggedIn: 'no',
      activeCoupon: '',
      products: [],
      productsImg: [],
      productsPrice: [],
      inCart: '0',
      productRender: [],
      trash: './trash.png',
      emptyCartAlert: '',
      showPaypal: 'hidePaypal',
      amount: [],
      amountId: [],
      currentSelect: ''
    };

    this.amILoggedIn = this.amILoggedIn.bind(this);
    this.getProductsInCart = this.getProductsInCart.bind(this);
    this.getImagesPrice = this.getImagesPrice.bind(this);
    this.getInCart = this.getInCart.bind(this);
    this.createProductRender = this.createProductRender.bind(this);
    this.addImages = this.addImages.bind(this);
    this.delProductFromCart = this.delProductFromCart.bind(this);
    this.reload = this.reload.bind(this);
    this.trashHover = this.trashHover.bind(this);
    this.trashNormal = this.trashNormal.bind(this);
    this.pressedCheckout = this.pressedCheckout.bind(this);
    this.getAmount = this.getAmount.bind(this);
    this.changeAmount = this.changeAmount.bind(this);
  }

  amILoggedIn () {
    fetch(API_SERVER + 'listen.php?part=amiloggedin&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['email'];
      if (tmp != "nodata") {
        this.setState({ myEmail: tmp, loggedIn: 'yes' });
      }
    })
    .catch(error => console.log(error.message));
  }
  getProductsInCart () {
    fetch(API_SERVER + 'listen.php?part=getproductsincart&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['products'];
      this.setState({ products: tmp });
      //console.log (this.state.products);
    })
    .catch(error => console.log(error.message));
  }
  getImagesPrice (e) {
    let idName = e;
    fetch(API_SERVER + 'listen.php?part=getproductdata&productname=' + idName)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['productdetails'];
      let pic = tmp['pic1'];
      let price = tmp['productprice'];
      this.setState(previousState => ({
        productsImg: [...previousState.productsImg, pic]
      }));
      this.setState(previousState => ({
        productsPrice: [...previousState.productsPrice, price]
      }));
    })
    .catch(error => console.log(error.message));
  }
  getInCart () {
    fetch(API_SERVER + 'listen.php?part=getproductsnumberincart&sessiontoken=' + session)
    .then(response => response.json())
		.then(output => {
      let data = output;
      let tmp = data['nr'];
      if (tmp == '0') { this.setState({ emptyCartAlert: <div><div className='spacer25px' /><p><i>Your cart is empty.</i> <br /><br /><a href="/love-and-light"><button className="startshoppingButton">START SHOPPING HERE</button></a></p></div> }); }
      this.setState({ inCart: tmp });
    })
    .catch(error => console.log(error.message));
  }
  createProductRender () {
    console.log (this.state.products);
    let nr = this.state.inCart;
    let prod = this.state.products;
    let images = this.state.productsImg;
    let prices = this.state.productsPrice;
    let amo = this.state.amount;
    let idName;
    let amount;
    let pricc;
    let id;
    let dataId;
    let size;
    let hlp;
    let imgsrc;
    let imgtmp;
    let i;
    let tmp = [];
    let s1;
    let s2;
    let s3;
    let s4;
    let s5;

    for (i=0; i<nr; i++) {
      id = prod[i]['id'];
      dataId = "d" + id;
      size = prod[i]['size'];
      idName = prod[i]['idname'];
      amount = amo[i]['tm1'];
      if (amount == '1') { s1 = <option value="1" selected>1</option>; } else { s1 = <option value="1">1</option>; }
      if (amount == '2') { s2 = <option value="2" selected>2</option>; } else { s2 = <option value="2">2</option>; }
      if (amount == '3') { s3 = <option value="3" selected>3</option>; } else { s3 = <option value="3">3</option>; }
      if (amount == '4') { s4 = <option value="4" selected>4</option>; } else { s4 = <option value="4">4</option>; }
      if (amount == '5') { s5 = <option value="5" selected>5</option>; } else { s5 = <option value="5">5</option>; }
      pricc = prices[i];
      imgtmp = images[i];
      imgsrc = API_SERVER + 'productphotos/' + imgtmp;
      console.log (imgtmp);
      tmp[i] = <div><div className="row" key={id}><div className="col-md-5"><img className="dyn" src={imgsrc} /></div>
      <div className="col-md-7">
        <div className="cartIconContainer"><div className="vertical-center">
        <table className="cartCo"><tr>
        <td>{idName}</td>
        <td>
          qty: <select id={dataId} className="amountButton" value={amount} onChange={this.changeAmount}>
            {s1}
            {s2}
            {s3}
            {s4}
            {s5}
          </select>
        </td>
        <td>{size}</td>
        <td>â‚¬{pricc}</td>
        <td><a data-id={id} href="#" onClick={this.delProductFromCart} onMouseEnter={this.trashHover} onMouseLeave={this.trashNormal}><img src={this.state.trash} width="35" height="35" /></a></td>
        </tr></table>
        </div></div>
      </div></div><hr /></div>
    }
    this.setState({ productRender: tmp });
  }
  addImages () {
    let nr = this.state.inCart;
    let prod = this.state.products;
    let hlp;
    let i;
    for (i=0; i<nr; i++) {
      hlp = prod[i]['idname'];
      this.getImagesPrice (hlp);
    }
  }
  delProductFromCart (e) {
    let id = e.currentTarget.dataset.id;
    fetch(API_SERVER + 'listen.php?part=delproductfromcart&id=' + id + '&sessiontoken=' + session, {mode: 'no-cors'})
    setTimeout(this.reload, 400);
  }
  reload () {
    window.location.href = "/checkout";
  }
  trashHover () {
    this.setState({ trash: './trash-b.png' });
  }
  trashNormal () {
    this.setState({ trash: './trash.png' });
  }
  pressedCheckout () {
    let loggedIn = this.state.loggedIn;
    if (loggedIn == 'no') {
      window.location.href = "/my-account";
    } else {
      this.setState({ showPaypal: 'showPaypal' });
    }
  }
  getAmount () {
    let nr = this.state.inCart;
    let prod = this.state.products;
    let amount;
    let id = [];
    let i;
    let tm1;
    let tm2;
    let tmp = [];
    let tmpId = [];
    for (i=0; i<nr; i++) {
      id[i] = prod[i]['id'];
      fetch(API_SERVER + 'listen.php?part=getamountincart&id=' + id[i] + '&pin=558240')
      .then(response => response.json())
  		.then(output => {
        let data = output;
        tm1 = data['amount'];
        tm2 = data['id'];
        tmp.push({ tm1 });
        tmpId.push({ tm2 });
        //console.log (this.state.products);
      })
      .catch(error => console.log(error.message));
    }
    this.setState({ amount: tmp }, () => { console.log (this.state.amount); });
    this.setState({ amountId: tmpId }, () => { console.log (this.state.amountId); });
  }
  changeAmount (e) {
    let idTmp = e.currentTarget.id;
    let id = idTmp.substr(1);
    let value = e.currentTarget.value;
    fetch(API_SERVER + 'listen.php?part=setamountincart&id=' + id + '&amount=' + value + '&pin=558240', {mode: 'no-cors'})
  }

  componentDidUpdate() {
    this.getInCart();
  }
  componentDidMount() {
    this.amILoggedIn();
    this.getProductsInCart();
    this.getInCart();
    setTimeout(this.addImages, 750);
    setTimeout(this.getAmount, 1500);
    setTimeout(this.createProductRender, 2500);
  }
  render() {
		return (
			<Container fluid>
      <Header />
        <Nav />
        <Ping />
        <div className="spacer50px" />
        <div className="row">
          <div className="col-md-12 ce capitalLetters">
            <h2><strong>Your Loved Pieces</strong></h2>
            {this.state.emptyCartAlert}
          </div>
        </div>
        <div className="spacer25px" />
        <div className="row">
          <div className="col-md-2" />
          <div className="col-md-8">
            Cart / {this.state.inCart} items
            <hr />
            {this.state.productRender}
            <div className="row">
              <div className="col-md-6">
                <div className="noBorder mediumFont"><a href="/love-and-light"><button className="startshoppingButton">CONTINUE SHOPPING</button></a></div>
              </div>
              <div className="col-md-6">
                <div className="noBorder mediumFont right"><button className="cartButton" onClick={this.pressedCheckout}>CHECKOUT</button></div>
                <div className={this.state.showPaypal}>
                  <PayPal />
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-2" />
        </div>


        <Footer />
      </Container>
		);
	}
}
